import React from "react";
import axios from "axios";

const query = `{ 
    id
    title
    paragraph
    images
}`;

const About = props => <h1>The response from the server is: {props.title}, {props.id}, {props.paragraph}, {props.images}</h1>;

About.getInitialProps = async () => {
  const response = await axios.post(`http://localhost:3000/api/about`, {
    query
  });

  console.log(response.data)

  return { ...response.data.data };
};

export default About;
