import { ApolloServer, gql } from 'apollo-server-micro'

const typeDefs = gql`
  type Query {
    about: [AboutMe!]!
  }
  type AboutMe {
    title: String,
    text: String, 
  }
  type TextContent {
    id: ID,
    text: String,
  }
`

const resolvers = {
  Query: {
    about(parent, args, context) {
      return [
        {
          title: 'Hit Network',
          text: {
            id: 1,
            text: 'Blah blah',
          },
      },
      ]
    },
  },
}

const apolloServer = new ApolloServer({ typeDefs, resolvers })

export const config = {
  api: {
    bodyParser: false,
  },
}

export default apolloServer.createHandler({ path: '/api/about' })
