import { ApolloServer, gql } from 'apollo-server-micro'

const typeDefs = gql`
  type Query {
    title: String,
    users: [User!]!
  }
  type User {
    text: String
  }
`

const resolvers = {
  Query: {
    title: 'About me',
    users(parent, args, context) {
      return [{ 
          text: 'Nextjs' 
        }]
    },
  },
}

const apolloServer = new ApolloServer({ typeDefs, resolvers })

export const config = {
  api: {
    bodyParser: false,
  },
}

export default apolloServer.createHandler({ path: '/api/about' })
